import { useEffect, useState } from "react";
import { Link as RouterLink } from "react-router-dom";
import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import Box from "@material-ui/core/Box";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import { BorrowInterface } from "../models/IBorrow";
import { format } from 'date-fns'


const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    container: {
      marginTop: theme.spacing(2),
    },
    table: {
      minWidth: 650,
    },
    tableSpace: {
      marginTop: 20,
    },
  })
);

function Borrow() {
  const classes = useStyles();
  const [Borrow, setBorrow] = useState<BorrowInterface[]>([]);
  const apiUrl = "http://localhost:8080";
  const requestOptions = {
    method: "GET",
    headers: { "Content-Type": "application/json" },
  };


  useEffect(() => {
    getBorrow();
  }, []);

  return (
    <div>
      <Container className={classes.container} maxWidth="md">
        <Box display="flex">
          <Box flexGrow={1}>
            <Typography
              component="h2"
              variant="h6"
              color="primary"
              gutterBottom
            >
              ระบบการยืมหนังสือ
            </Typography>
            <Box>
            <Button
              component={RouterLink}
              to="/borrow/create"
              variant="contained"
              color="primary"
            >
              สร้างข้อมูล
            </Button>
          </Box>
          </Box>
        </Box>
        <TableContainer component={Paper} className={classes.tableSpace}>
          <Table className={classes.table} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="center" width="8%">
                  รหัสการยืม
                </TableCell>
                <TableCell align="center" width="15%">
                  Email
                </TableCell>
                <TableCell align="center" width="15%">
                  ประเภทการยืม
                </TableCell>
                <TableCell align="center" width="15%">
                  รหัสหนังสือ
                </TableCell>
                <TableCell align="center" width="30%">
                  วันที่ยืมหนังสือ
                </TableCell>
                <TableCell align="center" width="15%">
                  วันที่ต้องคืนหนังสือ
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {Borrow.map((item: BorrowInterface) => (
                <TableRow key={item.ID}>
                  <TableCell align="center">{item.ID}</TableCell>
                  <TableCell align="center">{item.Member.Email}</TableCell>
                  <TableCell align="center">{item.Btype.Name}</TableCell>
                  <TableCell align="center">{item.Book.ID}</TableCell>
                  <TableCell align="center">{format((new Date(item.Borrow_date)), 'dd MMMM yyyy hh:mm a')}</TableCell>
                  <TableCell align="center">{format((new Date(item.Due_date)), 'dd MMMM yyyy hh:mm a')}</TableCell>

                </TableRow>
              ))}
            </TableBody>
          </Table> 
        </TableContainer>
      </Container>
    </div>
  );
}

export default Borrow;
