import { useEffect, useState } from "react";
import { Link as RouterLink } from "react-router-dom";
import {
  makeStyles,
  Theme,
  createStyles,
  alpha,
} from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import FormControl from "@material-ui/core/FormControl";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import Snackbar from "@material-ui/core/Snackbar";
import Select from "@material-ui/core/Select";
import MuiAlert, { AlertProps } from "@material-ui/lab/Alert";

import { BookInterface } from "../models/IBook";
import { BorrowInterface } from "../models/IBorrow";
import { BtypeInterface } from "../models/IBtype";
import { MemberInterface } from "../models/IMember";
import {
  MuiPickersUtilsProvider,
  KeyboardDateTimePicker,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";

const Alert = (props: AlertProps) => {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
};

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1,
    },
    container: {
      marginTop: theme.spacing(2),
    },
    paper: {
      padding: theme.spacing(2),
      color: theme.palette.text.secondary,
    },
  })
);

function CreateBorrow() {
  const classes = useStyles();
  const [selectedDate, setSelectedDate] = useState<Date | null>(new Date());
  const [due, setDue] = useState<Date | null>(new Date());
  const [members, setMember] = useState<MemberInterface[]>([]);
  const [books, setBook] = useState<BookInterface[]>([]);
  const [btypes, setBtype] = useState<BtypeInterface[]>([]);
  const [pday, setPday] = useState<BtypeInterface[]>([]);
  const [borrows, setBorrow] = useState<Partial<BorrowInterface>>(
    {}
  );

  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);

  const apiUrl = "http://localhost:8080";
  const requestOptions = {
    method: "GET",
    headers: { "Content-Type": "application/json" },
  };

  const handleClose = (event?: React.SyntheticEvent, reason?: string) => {
    if (reason === "clickaway") {
      return;
    }
    setSuccess(false);
    setError(false);
  };

  const handleChange = (
    event: React.ChangeEvent<{ name?: string; value: unknown }>
  ) => {
    const name = event.target.name as keyof typeof borrows;
    setBorrow({
      ...borrows,
      [name]: event.target.value,
    });
  };

  const handleDateChange = (date: Date | null) => {
    console.log(date);
    setSelectedDate(date);
  };
  const handleDateChange2 = (date: Date | null) => {
    console.log(date);
    setDue(date);
  };

  const getMember = async () => {
    fetch(`${apiUrl}/member`, requestOptions)
      .then((response) => response.json())
      .then((res) => {
        if (res.data) {
          setMember(res.data);
        } else {
          console.log("else");
        }
      });
  };

  const getBook = async () => {
    fetch(`${apiUrl}/book`, requestOptions)
      .then((response) => response.json())
      .then((res) => {
        if (res.data) {
          setBook(res.data);
        } else {
          console.log("else");
        }
      });
  };

  const getBtype = async () => {
    fetch(`${apiUrl}/btype`, requestOptions)
      .then((response) => response.json())
      .then((res) => {
        if (res.data) {
          setBtype(res.data);
        } else {
          console.log("else");
        }
      });
  };
  const getPday = async() =>{
    fetch(`${apiUrl}/day/${borrows.BtypeID}`, requestOptions)
      .then((response) => response.json())
      .then((res) => {
        if (res.data) {
          setPday(res.data)
        } else {
          console.log("else");
        }
      });
  }
  

  
  useEffect(() => {
    getMember();
    getBook();
    getBtype();
  }, []);

  const convertType = (data: string | number | undefined) => {
    let val = typeof data === "string" ? parseInt(data) : data;
    return val;
  };


  function submit() {
    var a = 0;
    fetch(`${apiUrl}/day/${borrows.BtypeID}`, requestOptions)
      .then((response) => response.json())
      .then((res) => {
        if (res.data) {
          a = res.data
        } else {
          console.log("else");
        }
      });
    var date = new Date();
    if(selectedDate != null){
      date.setDate(selectedDate.getDate() + a);
    }

    let data = {
      MemberID : convertType(borrows.MemberID),
      BookID: convertType(borrows.BookID),
      BtypeID: convertType(borrows.BtypeID),
      Borrow_Date: selectedDate,
      Due_date: due,
    };

    const requestOptionsPost = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    };

    fetch(`${apiUrl}/borrow`, requestOptionsPost)
      .then((response) => response.json())
      .then((res) => {
        if (res.data) {
          setSuccess(true);
        } else {
          setError(true);
        }
      });
  }

  return (
    <Container className={classes.container} maxWidth="md">
      <Snackbar open={success} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="success">
          บันทึกข้อมูลสำเร็จ
        </Alert>
      </Snackbar>
      <Snackbar open={error} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="error">
          บันทึกข้อมูลไม่สำเร็จ
        </Alert>
      </Snackbar>
      <Paper className={classes.paper}>
        <Box display="flex">
          <Box flexGrow={1}>
            <Typography
              component="h2"
              variant="h6"
              color="primary"
              gutterBottom
            >
              บันทึกการยืมหนังสือ
            </Typography>
          </Box>
        </Box>
        <Divider />
        <Grid container spacing={3} className={classes.root}>
        <Grid item xs={12}>
            <FormControl fullWidth variant="outlined">
              <p>รหัสสมาชิก</p>
              <Select
                native
                value={borrows.MemberID}
                onChange={handleChange}
                inputProps={{
                  name: "MemberID",
                }}
              >
                <option aria-label="None" value="">
                  กรุณาระบุรหัสสมาชิก
                </option>
                {members.map((item: MemberInterface) => (
                  <option value={item.ID} key={item.ID}>
                    รหัสสมาชิก {item.ID} E-mail {item.Email}
                  </option>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth variant="outlined">
              <p>รหัสหนังสือ</p>
              <Select
                native
                value={borrows.BookID}
                onChange={handleChange}
                inputProps={{
                  name: "BookID",
                }}
              >
                <option aria-label="None" value="">
                  กรุณาระบุรหัสหนังสือ
                </option>
                {books.map((item: BookInterface) => (
                  <option value={item.ID} key={item.ID}>
                    รหัสหนังสือ {item.ID} ชื่อหนังสือ {item.Name}
                  </option>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth variant="outlined">
              <p>ประเภทการยืม</p>
              <Select
                native
                value={borrows.BtypeID}
                onChange={handleChange}
                inputProps={{
                  name: "BtypeID",
                }}
              >
                <option aria-label="None" value="">
                  กรุณาประเภทการยืม
                </option>
                {btypes.map((item: BtypeInterface) => (
                  <option value={item.ID} key={item.ID}>
                    รหัสประเภทการยืม {item.ID} ชื่อประเภทการยืม {item.Name} จำนวนเงินที่ต้องชำระ {item.Price} บาท จำนวนวันที่ยืมได้ {item.Period_Day} วัน
                  </option>
                ))}

              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={6}>
            <FormControl fullWidth variant="outlined">
              <p>วันที่และเวลาที่เริ่มต้นยืม</p>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDateTimePicker
                  name="Borrow_Date"
                  value={selectedDate}
                  onChange={handleDateChange}
                  label="กรุณาเลือกวันที่และเวลาเริ่มต้นยืม"
                  minDate={new Date("2018-01-01T00:00")}
                  format="yyyy/MM/dd hh:mm a"
                />

              </MuiPickersUtilsProvider>
            </FormControl>
          </Grid>
          <Grid item xs={6}>
            <FormControl fullWidth variant="outlined">
              <p>วันที่และเวลาที่เริ่มต้นยืม</p>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDateTimePicker
                  name="Due_Date"
                  value={due}
                  onChange={handleDateChange}
                  label="กรุณาเลือกวันที่และเวลาเริ่มต้นยืม"
                  minDate={new Date("2018-01-01T00:00")}
                  format="yyyy/MM/dd hh:mm a"
                />
              </MuiPickersUtilsProvider>
            </FormControl>
          </Grid>
        
         
          <Grid item xs={12}>
            <Button
              component={RouterLink}
              to="/borrow"
              variant="contained"
            >
              กลับ
            </Button>
            <Button
              style={{ float: "right" }}
              variant="contained"
              onClick={submit}
              color="primary"
            >
              บันทึก
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
}

export default CreateBorrow;
